    //Adquire as variaveis, valida e cria um objeto Ocorrencia
    private void bntSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bntSalvarActionPerformed
        int chamado = Integer.parseInt(txtChamado.getText());
        boolean conseguiu;
        String lab = (String) cmbLab.getSelectedItem();
        String comp = (String) cmbComputador.getSelectedItem();
        String problema = "";
        if (jCheckBox1.isSelected()) {
            problema = "Hardware";
        }
        if (jCheckBox2.isSelected()) {
            problema = "Software";
        }
        if (lab.equals("") || problema.equals("") || lab.equalsIgnoreCase(">> Selecione um Lab <<")) {
            JOptionPane.showMessageDialog(this, "Todos os campos devem ser preenchidos!!");
        } else {

            DAO dao = new DAO();
            Ocorrencia ocorrencia = new Ocorrencia(chamado, lab, comp, problema);
            conseguiu = dao.inserirOcorrencia(ocorrencia);
            if (conseguiu) {
                JOptionPane.showMessageDialog(this, "A ocorrencia foi registrada no banco!");
            }
        }
    }//GEN-LAST:event_bntSalvarActionPerformed

