    /*
     *  Adquire o nº do chamado
     *  Chama o DAO.pesquisarOcorrencia
     *  Seta os outros campos com os valores encontrados no banco
     */
    private void btnPesquisarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPesquisarActionPerformed
        int chamado = Integer.parseInt(txtChamado.getText());
        DAO dao = new DAO();
        Ocorrencia aux;
        aux = dao.pesquisarOcorrencia(chamado);

        if (aux != null) {
            cmbLab.setSelectedItem(aux.getLab());
            cmbComputador.setSelectedItem(String.valueOf(aux.getComp()));
            if (aux.getProblema().equals("Hardware")) {
                jCheckBox1.setSelected(true);
            } else {
                jCheckBox2.setSelected(true);
            }
        } else {
            JOptionPane.showMessageDialog(this, "Não foi possivel achar o chamado no banco!");
        }

    }//GEN-LAST:event_btnPesquisarActionPerformed

    //Atualiza a ComboBox com os laboratorios já cadastrados
    private void cmbLabMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_cmbLabMousePressed
        atualizarLista();
    }//GEN-LAST:event_cmbLabMousePressed

    /*
     *  Chama o DAO.pesquisarLaboratorio
     *  Remove e atualiza o ComboBox com os dados da lista encontrados no banco
     */
    public void atualizarLista() {
        cmbLab.removeAllItems();
        cmbLab.addItem(">> Selecione um Lab <<");
        DAO dao = new DAO();
        ArrayList<Laboratorio> labList = dao.pesquisarLaboratorio();
        String aux = null;
        for (int i = 0; i < labList.size(); i++) {
            aux = labList.get(i).getLab();
            cmbLab.addItem(aux);
        }
    }

